import React, { Component } from 'react';
import { AppCard } from '../common/card';
import { Room } from './room';
import styled from 'styled-components';

const BookingContainer = styled.div`
display: flex;
`;

export class Booking extends Component {
    constructor() {
        super();
    }
    componentDidMount() {
    }

    onSelectionChange(room, selected) {
        const { rooms, selectRoom, deSelectRoom, resetSelection } = this.props;

        // resetSelection();
        let reached = false;
        rooms.forEach(_room => {
            if (reached) {
                deSelectRoom(_room.roomId);
            }

            if (!reached && _room.available && selected) {
                selectRoom(_room.roomId);
            }

            if (!reached && (_room.roomId === room.roomId)) {
                reached = true;
            }
        });
    }

    render() {
        const { rooms, selectedRoomIds = [], selectOccupant } = this.props;

        return (<BookingContainer>{
            rooms.map((room) => {
                return <Room {...room}
                    selectOccupant={selectOccupant}
                    selected={selectedRoomIds.includes(room.roomId)}
                    onSelectionChange={this.onSelectionChange.bind(this, room)} />
            })
        }</BookingContainer>);
    }
}
